function preLoader() {
    const tl = gsap.timeline({ duration: 0.1 });
    tl.to(".preLoader.black > img", {
        y: 50,
        autoAlpha: 0,
    })
        .to(".preLoader.black", {
            yPercent: -100,
        })
        .to(".preLoader.white", {
            yPercent: -100,
        })
        .to(".preLoader", {
            css: { display: "none", },
        })
        .from(".mouse", {
            y: -50,
            autoAlpha: 0,
        })
        .from(".navbar-brand > img", {
            x: -50,
            autoAlpha: 0,
        }, "-=1")
        .from(".navbar-nav > li, .form-inline a", {
            autoAlpha: 0,
            x: 100,
            stagger: 0.05,
        }, "-=1")
        .from(".slideOne", {
            autoAlpha: 0,
            yPercent: 100,
            stagger: 0.05,
        }, "-=1");
}
//  CUSTOM MOUSE CURSOR

function mouseCursor() {
    if (document.querySelector(".cursor-inner") || document.querySelector(".cursor-outer")) {
        const cursorInner = document.querySelector(".cursor-inner");
        const cursorOuter = document.querySelector(".cursor-outer");
        let mouseX = 0;
        let mouseY = 0;
        let isHovering = false;

        window.addEventListener("mousemove", function (event) {
            if (!isHovering) {
                gsap.to(cursorOuter, {
                    x: event.clientX,
                    y: event.clientY,
                });
            }
            gsap.to(cursorInner, {
                x: event.clientX,
                y: event.clientY,
            });
            mouseY = event.clientY;
            mouseX = event.clientX;
        });

        const elementsToTrack = document.querySelectorAll("a, img, .cursor-pointer");
        elementsToTrack.forEach((element) => {
            element.addEventListener("mouseenter", function () {
                cursorInner.classList.add("cursor-hover");
                cursorOuter.classList.add("cursor-hover");
                isHovering = true;
            });

            element.addEventListener("mouseleave", function () {
                if (!(
                    element.tagName === "A" ||
                    element.tagName === "IMG" ||
                    element.closest(".cursor-pointer")
                )) {
                    cursorInner.classList.remove("cursor-hover");
                    cursorOuter.classList.remove("cursor-hover");
                }
                isHovering = false;
            });
        });

        gsap.set(cursorInner, {
            visibility: "visible"
        });
        gsap.set(cursorOuter, {
            visibility: "visible"
        });
    }
}

// ANCHOR VOID MANIPULATE #

function replaceHash() {
    document.querySelectorAll("a").forEach((a) => {
        let href = a.getAttribute("href");
        a.href = href ?
            href.startsWith("#") && href.endsWith("#") ?
                href.replace("#", "javascript:;") :
                href :
            "javascript:;";
    });
}

// GSAP IMAGE REVEAL ANIMATION

function imgRevealer() {
    let revealContainers = document.querySelectorAll(".reveal");

    revealContainers.forEach((container) => {
        let image = container.querySelector("img");
        let revelerTl = gsap.timeline({
            scrollTrigger: {
                // scroller: '[data-scroll-container]',
                trigger: container,
                toggleActions: "restart none none reverse",
            },
        });

        revelerTl.set(container, {
            autoAlpha: 1
        });
        revelerTl.from(container, 0.75, {
            xPercent: -100,
            ease: Power2.out,
        });
        revelerTl.from(image, 0.75, {
            xPercent: 100,
            scale: 1.3,
            delay: -0.75,
            ease: Power2.out,
        });
    });
}

// GSAP HEADING ANIMATION

function headingAnimation() {
    const heading = document.querySelectorAll(".mainHead");

    heading.forEach((text) => {
        let text1 = new SplitType(text, {
            types: "chars",
        });
        const headingTl = gsap.timeline({
            scrollTrigger: {
                trigger: text,
                toggleActions: "play none none reset",
            },
        });
        let chars = text.querySelectorAll(`.mainHead .char`);
        headingTl.from(chars, 1, {
            x: 50,
            autoAlpha: 0,
            stagger: 0.1,
            webkitFilter: "blur(20px)",
            ease: "power4.out",
        });
    });
}



// Cunter  Java Scrpt Start

const counters = document.querySelectorAll(".counter");

counters.forEach(counter => {
    const updateCount = () => {
        const target = +counter.getAttribute("data-target");
        const current = +counter.innerText;

        // âœ… Increase speed value for slower animation
        const speed = 7000;
        const increment = target / speed;

        if (current < target) {
            counter.innerText = Math.ceil(current + increment);
            setTimeout(updateCount, 25); // Delay also increased for smoother slower effect
        } else {
            counter.innerText = target;
        }
    };

    updateCount();
});



// Cunter  Java Scrpt End 



// Tabs Java Script Start

$('#tabs-nav li:first-child').addClass('active');
$('.tab-content').hide();
$('.tab-content:first').show();

$('#tabs-nav li').click(function () {
    $('#tabs-nav li').removeClass('active');
    $(this).addClass('active');
    $('.tab-content').hide();

    var activeTab = $(this).find('a').attr('href');
    $(activeTab).fadeIn();
    return false;
});

