// LENIS SETUP

function lenissetup() {
    const lenis = new Lenis()

    function raf(time) {
        lenis.raf(time)
        requestAnimationFrame(raf)
    }
    requestAnimationFrame(raf)
}

// Function Initialization
function initializeFunctions() {
    preLoader();
    allSliders();
    mouseCursor();
    replaceHash();
    // Animation EFFECTs
    imgRevealer();
    headingAnimation();

    // LENIS 
    lenissetup();

    // AOS INIT
    AOS.init({
        duration: 1200,
        disable: 'mobile'
    });
}

// Event listener for window load
window.addEventListener("load", initializeFunctions);